mapper.select:
select

from latitude_longitude


    tacticskq_id = #{p.tacticskqId,jdbcType=VARCHAR}


    and validity_period  >=  #{p.startValidityPeriod}


    and validity_period  <= = #{p.endValidityPeriod}


    and
    ((lat_min  <=  #{p.latAndlng,jdbcType=DECIMAL} and lat_max  >=  #{p.latAndlng,jdbcType=DECIMAL})
    or (lng_min  <=  #{p.latAndlng,jdbcType=DECIMAL} and lng_max  >=  #{p.latAndlng,jdbcType=DECIMAL}))
